mapper.insert:
	INSERT INTO coaching_RFQ_result
		(coaching_RFQ_result_code
		, coaching_category_code
		, coaching_RFQ_code
		, total_plan_code
		, plan_detail_code
		, coach_user_email
		, user_email
		, coaching_RFQ_result_cost
		, coaching_RFQ_result_day
		, coaching_RFQ_result_costPerHour
		, coaching_RFQ_result_time
		, coaching_RFQ_result_method
		, coaching_RFQ_result_area
		, coaching_RFQ_result_agreement
		, coaching_RFQ_result_service_start_date
		, coaching_RFQ_result_service_end_date
		, coaching_RFQ_result_register_date)
	VALUES (
				#{coachingRFQResultCode}
				, #{coachingCategoryCode}
				, #{coachingRFQCode}
				, null
				, null
				, #{coachUserEmail}
				, #{userEmail}
				, 0
				, #{coachingRFQResultDay}
				, #{coachingRFQResultCostPerHour}
				, #{coachingRFQResultTime}
				, #{coachingRFQResultMethod}
				, NULL
				, #{coachingRFQResultAgreement}			
				, #{coachingRFQResultServiceStartDate}
				, #{coachingRFQResultServiceEndDate}
				, (SELECT DATE_FORMAT(CURDATE(),'%Y-%m-%d'))
			)

mapper.select:
SELECT * FROM coaching_RFQ AS q LEFT JOIN coaching_RFQ_result AS r ON q.coaching_RFQ_code = r.coaching_RFQ_code
LEFT JOIN coaching_apply_and_payment AS a ON q.coaching_RFQ_code = a.coaching_RFQ_code INNER JOIN coach_user AS c ON q.coach_user_email = c.coach_user_email INNER JOIN user AS u ON q.user_email = u.user_email
WHERE q.coach_user_email = #{coachUserEmail};
WHERE q.user_email = #{userEmail};
;

SELECT u.coach_user_email ,u.coach_password ,u.coach_name ,u.coach_birth ,u.coach_addr ,u.coach_addr_detail ,u.coach_gender ,u.coach_mobile ,u.coach_authority ,u.coach_job ,u.coach_profile_career ,u.coach_profile_tech_stack ,u.coach_certificate ,u.coach_career_documentary_evidence ,u.coach_certificate_documentary_evidence ,u.coach_join_approval ,u.coach_approver_email ,u.coach_is_receive_marketing ,u.coach_join_motivation ,u.coach_join_path ,u.coach_recommend_email ,u.coach_join_date ,u.coach_withdraw_status
,m.coach_management_code ,m.coach_user_email ,m.coaching_introduce_subject ,m.coaching_introduce_contents ,m.coach_management_count ,m.coach_management_review ,m.coach_management_rate ,m.coach_management_hourly_average_price ,m.coach_register_date ,m.coach_blog_github_adress FROM coach_user AS u left JOIN coach_management AS m ON u.coach_user_email = m.coach_user_email
WHERE coach_authority = #{coachAuthority}
WHERE u.coach_user_email = #{coachUserEmail}
